import React, { useState, useEffect, useRef } from "react";

function TodoForm(props) {
    const [input, setInput] = useState(props.edit ? props.edit.value : '');

    const inputRef = useRef(null)

    useEffect(() => {
        inputRef.current.focus()
    })

    const handleChange = e => {
        setInput(e.target.value);
    }

    // When adding a todo it doesnt refresh the page
    const handleSubmit = e => {
        e.preventDefault();

        props.onSubmit({
            id: Math.floor(Math.random() * 10000),
            text: input
        });

        setInput('');
    }

    return (
        <div>
        <form  onSubmit={handleSubmit} className="todo-form">
          {props.edit ? (
            <>
              <input

                placeholder="Update list"
                value={input}
                name="text"

                onChange={handleChange}
                ref={inputRef}
                className="todo-input edit"
              />
              <button onClick={handleSubmit} className="todo-button edit">Update todo</button>
            </>
          ) : (
            <>
              <input

                placeholder="Add a todo"
                value={input}


                onChange={handleChange}
                name="text"
                className="todo-input"
                ref={inputRef}
              />
              <button onClick={handleSubmit} className="todo-button">Add todo</button>
            </>
          )}
        </form>
      </div>
    )  
}

export default TodoForm;
